import { useState } from 'react';

const Form = (handleAddNewUserSubmit, ClickAdd, name, age, hometown, setname, setage, sethometown) => {
    return(
        <div>
            <h2>Add User</h2>
            <form onSubmit={handleAddNewUserSubmit}>
                <label>Name:</label>
                <input 
                    type="text" 
                    required 
                    value={name}
                    onChange={setname}
                />
                <label>Age:</label>
                <input 
                    type="number" 
                    required 
                    value={age}
                    onChange={setage}
                />
                <label>Hometown:</label>
                <input 
                    type="text" 
                    required 
                    value={hometown}
                    onChange={sethometown}
                />
                <button>Add User</button>
                <button onClick={ClickAdd}>Close Form</button>
            </form>
        </div>
    )
}
export default Form;
