import './sidebar-navigation.css';
import home from '../../../assets/images/home.png';
import course from '../../../assets/images/course.png';
import students from '../../../assets/images/students.png';
import payment from '../../../assets/images/payment.png';
import report from '../../../assets/images/report.png';
import settings from '../../../assets/images/settings.png';
import { useState } from 'react';
import { SidebarNavigationData } from './sidebar-navigation-data';

const SidebarNavigation = () => {
    const [isActive, setIsActive] = useState(false);
    const handleClick = () => {
        setIsActive(current => !current);
    }
    return(
        <div className="sidebar-navigation">
            <div className={isActive ? 'button' : null} onClick={handleClick}>
                <div className='home'>
                    <img src={home} alt='icon'/>
                    <p>Home</p>
                </div>
            </div>
            <div className={isActive ? 'button' : null} onClick={handleClick}>
                <div className='course'>
                    <img src={course} alt='icon'/>
                    <p>Course</p>
                </div>
            </div>
            <div className={isActive ? 'button' : null} onClick={handleClick}>
                <div className='students'>
                    <img src={students} alt='icon'/>
                    <p>Students</p>
                </div>
            </div>
            <div className={isActive ? 'button' : null} onClick={handleClick}>
                <div className='payment'>
                    <img src={payment} alt='icon'/>
                    <p>Payment</p>
                </div>
            </div>
            <div className={isActive ? 'button' : null} onClick={handleClick}>
                <div className='report'>
                    <img src={report} alt='icon'/>
                    <p>Report</p>
                </div>
            </div>
            <div className={isActive ? 'button' : null} onClick={handleClick}>
                <div className='settings'>
                    <img src={settings} alt='icon'/>
                    <p>Settings</p>
                </div>
            </div>
        </div>
    )
}
export default SidebarNavigation;
