import { useState } from 'react';
import axiosInstance from '../../axios/axios-instance';

export const useMainPanelHook = () => {
    const [users, setUsers] = useState([]);
    const [isAddNewUserShown, setIsAddNewUserShown] = useState(false);
    const [isEditUserShown, setIsEditUserShown] = useState(false);

    const getAllUsers = async () => {
        try {
            await axiosInstance({
                url: "/users",
                method: "GET",
            }).then((res) => {
                setUsers(res.data);
            });
        } catch (e) {
        console.error(e);
        }
    }

    const EditUser = async (id) => {
        try {
            await axiosInstance({
                url: `/users/${id}`,
                method: "PATCH",
            }).then((res) => {
                setUsers(res.data);
            });
        } catch (e) {
        console.error(e);
        }
    }

    const deleteUser = async (id) => {
        try {
            await axiosInstance({
                url: `/users/${id}`,
                method: "DELETE",
            }).then(getAllUsers());
        } catch (e) {
        console.error(e);
        }
    }

    const createUser = async (data) => {
        try {
            await axiosInstance({
                url: `/users`,
                method: "POST",
                data: data
            }).then(getAllUsers());
        } catch (e) {
        console.error(e);
        }
    }

    return {
        getAllUsers,
        EditUser,
        deleteUser,
        createUser,
        users,
        isAddNewUserShown,
        setIsAddNewUserShown,
        isEditUserShown,
        setIsEditUserShown
    }
}
