import './main-panel.css';
import edit from '../../assets/images/edit.png';
import deleteIcon from '../../assets/images/delete.png';
import { useEffect } from 'react';
import { useMainPanelHook } from './main-panel.hook';

const MainPanel = () => {
    const { getAllUsers, createUser, deleteUser, users, isAddNewUserShown, setIsAddNewUserShown } = useMainPanelHook();

    useEffect(() => {
        getAllUsers();
    }, [getAllUsers]);
    return(
        isAddNewUserShown ? <Create /> : <div className="main-panel">
        <div className='header-container'>
            <p className='text'>Students List</p>
            <div className='add-new-student-button' onClick={() => createUser()}>
                <p className='button-text'>ADD NEW STUDENT</p>
            </div>
        </div>
        <div className='horizontal-line'/>
        <div className='table-header-container'>
            <p>Name</p>
            <p>Age</p>
            <p>Hometown</p>
        </div>
        {users.map((item, index) => {
                return (
                <div key={index} className='list-item'>
                    <div className='list-item-data'>
                        <p>{item.name}</p>
                        <p>{item.age}</p>
                        <p>{item.hometown}</p>
                    </div>
                    <div className='icon-container'>
                        <img src={edit} alt="icon" className='edit'/>
                        <img src={deleteIcon} alt="icon" className='delete' onClick={() => deleteUser(item.key)}/>
                    </div>
                </div>
                )
            })}
    </div>

    )
}
export default MainPanel;
