import { useState } from 'react';
import axiosInstance from '../../axios/axios-instance';

export const useMainPanelHook = () => {
    const [users, setUsers] = useState([]);

    const getAllUsers = async () => {
        try {
            await axiosInstance({
                url: "/users",
                method: "GET",
            }).then((res) => {
                setUsers(res.data);
            });
        } catch (e) {
        console.error(e);
        }
    }
    return {
        getAllUsers,
        users
    }
}
