import './main-panel.css';
import edit from '../../assets/images/edit.png';
import deleteIcon from '../../assets/images/delete.png';
import { useEffect } from 'react';
import { useMainPanelHook } from './main-panel.hook';
import { useState } from 'react';

const MainPanel = () => {
    const { getAllUsers, createUser, deleteUser, users, isAddNewUserShown, setIsAddNewUserShown } = useMainPanelHook();

    useEffect(() => {
        getAllUsers();
    }, [getAllUsers]);
    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const [hometown, setHometown] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const newUser = { name, age, hometown };

        createUser(newUser);
    }
    return(
        isAddNewUserShown ? (<div>
            <h2>Add New User</h2>
            <form onSubmit={handleSubmit}>
              <label>Name:</label>
              <input 
                type="text" 
                required 
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
              <label>Age:</label>
              <input 
                type="number" 
                required 
                value={age}
                onChange={(e) => setAge(e.target.value)}
              />
              <label>Hometown:</label>
              <input 
                type="text" 
                required 
                value={hometown}
                onChange={(e) => setHometown(e.target.value)}
              />
              <button>Add User</button>
            </form>
          </div>) : (<div className="main-panel">
        <div className='header-container'>
            <p className='text'>Students List</p>
            <div className='add-new-student-button' onClick={() => setIsAddNewUserShown(!isAddNewUserShown)}>
                <p className='button-text'>ADD NEW STUDENT</p>
            </div>
        </div>
        <div className='horizontal-line'/>
        <div className='table-header-container'>
            <p>Name</p>
            <p>Age</p>
            <p>Hometown</p>
        </div>
        {users.map((item, index) => {
                return (
                <div key={index} className='list-item'>
                    <div className='list-item-data'>
                        <p>{item.name}</p>
                        <p>{item.age}</p>
                        <p>{item.hometown}</p>
                    </div>
                    <div className='icon-container'>
                        <img src={edit} alt="icon" className='edit'/>
                        <img src={deleteIcon} alt="icon" className='delete' onClick={() => deleteUser(item.key)}/>
                    </div>
                </div>
                )
            })}
    </div>)   
    )
}
export default MainPanel;
