const UserForm = (name, age, hometown, onSubmitForm, onChangeName, onChangeAge, onChangeHometown, onClose) => {
    return(
        <div>
            <h2>Add New User</h2>
            <form onSubmit={onSubmitForm}>
              <label>Name:</label>
              <input 
                type="text" 
                required 
                value={name}
                onChange={(e) => onChangeName(e.target.value)}
              />
              <label>Age:</label>
              <input 
                type="number" 
                required 
                value={age}
                onChange={(e) => onChangeAge(e.target.value)}
              />
              <label>Hometown:</label>
              <input 
                type="text" 
                required 
                value={hometown}
                onChange={(e) => onChangeHometown(e.target.value)}
              />
              <button>Add User</button>
              <button onClick={onClose}>Close Form</button>
            </form>
          </div>
    )
}
export default UserForm;
