import { useState } from 'react';
import { useMainPanelHook } from '../main-panel/main-panel.hook';

const UserForm = (type, onSubmit) => {
    const { getUser, getAllUsers, createUser, editUser, deleteUser, user, setUser, users, isAddNewUserShown, setIsAddNewUserShown, isEditUserShown, setIsEditUserShown } = useMainPanelHook();
    const [name, setName] = useState('');
    const [age, setAge] = useState('');
    const [hometown, setHometown] = useState('');
    const handleAddNewUserSubmit = (e) => {
        e.preventDefault();
        const newUser = { name, age, hometown };
        createUser(newUser);
        setIsAddNewUserShown(!isAddNewUserShown);
        setName('');
        setAge('');
        setHometown('');
    }
    return(
        <div>
            <h2>{type}</h2>
            <form onSubmit={onSubmit}>
              <label>Name:</label>
              <input 
                type="text" 
                required 
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
              <label>Age:</label>
              <input 
                type="number" 
                required 
                value={age}
                onChange={(e) => setAge(e.target.value)}
              />
              <label>Hometown:</label>
              <input 
                type="text" 
                required 
                value={hometown}
                onChange={(e) => setHometown(e.target.value)}
              />
              <button>Add User</button>
            </form>
          </div>
    )
}
export default UserForm;
